              const menuButton = document.getElementById('headlessui-switch-:Raalda:');
          
              if (!menuButton) {
                return;
              }
          
              // Find all the <span> elements inside the menuButton
              const menuButtonSpans = menuButton.querySelectorAll('span');
          
              // Add event listener for the menu button
              menuButton.addEventListener('click', () => {
                // Find all elements inside <header> with data-isopen attribute
                const menuItems = document.querySelectorAll('header [data-isopen]');
                
                // Toggle data-isopen for the menu items
                if (menuItems.length > 0) {
                  menuItems.forEach(item => {
                    const isOpen = item.getAttribute('data-isopen') === 'true';
                    const newState = isOpen ? 'false' : 'true';
                    item.setAttribute('data-isopen', newState);
                  });
                }
          
                // Toggle data-checked for <span> elements inside the button
                if (menuButtonSpans.length > 0) {
                  menuButtonSpans.forEach(span => {
                    const spanState = span.getAttribute('data-checked') === 'true';
                    const spanNewState = spanState ? 'false' : 'true';
                    span.setAttribute('data-checked', spanNewState);
                  });
                }
          
                // Optionally, toggle the overflow-hidden class on the document element (for hiding scroll)
                const isMenuOpen = menuItems.length > 0 && menuItems[0].getAttribute('data-isopen') === 'true';
                document.documentElement.classList.toggle('overflow-hidden', isMenuOpen);
              });
            });
          </script>  <!--End Mobile Menu Opener-->
          
       <!--Dark theme switcher -->
          <script>
              document.addEventListener("DOMContentLoaded", function() {
    const themeToggleButtonmob = document.getElementById("themeToggleButtonmob");
    const themeToggleButton = document.getElementById("themeToggleButton");
    const htmlElement = document.documentElement;

    // Check the saved theme from localStorage (if any)
    if (localStorage.getItem("theme") === "dark") {
        htmlElement.classList.add("dark");
    }

    // Add an event listener for the mobile theme toggle button
    themeToggleButtonmob.addEventListener("click", () => {
        htmlElement.classList.toggle("dark");
    });

    // Add an event listener for the desktop theme toggle button
    themeToggleButton.addEventListener("click", () => {
        htmlElement.classList.toggle("dark");

        // Save the current theme in localStorage
        if (htmlElement.classList.contains("dark")) {
            localStorage.setItem("theme", "dark");
        } else {
            localStorage.setItem("theme", "light");
        }
    });
});
</script> <!--End Dark theme switcher -->
